Scripts.OnEvent:
if ZGV.NotificationCenter then self:Hide() return end if event=="PLAYER_TARGET_CHANGED" then self:Hide() --Hide unless shown otherwise if ZGV.db.profile.detectcreatures and (ZGV.guidesets['PetsMountsA'] or ZGV.guidesets['PetsMountsH']) then ZGV.CreatureDetector:Detect() else ZGV.CreatureDetector.DetectedGuides=nil end
if ZGV.CreatureDetector.DetectedGuides then if ZGV.db.profile.sb_enabled and ZGV.SlideBar then local data = { OnClick = function() self:Click() end, OnEnter = function(button) local position,x,y = ZGV.SlideBar.private.lastConfig.anchor or "top",0,0 if position=="top" then position="ANCHOR_BOTTOM" y=-5 elseif position=="bottom" then position="ANCHOR_TOP" y=5 end if position=="left" then position="ANCHOR_RIGHT" elseif position=="right" then position="ANCHOR_LEFT" end ZGV.CreatureDetector:ShowTooltip(button,position,x,y) end, OnLeave = function() GameTooltip:Hide() end, } local texcoords = {self.ntx:GetTexCoord()}
ZGV.SlideBar.AddButton(self:GetName(),self.ntx:GetTexture(),1,"",nil,nil,data,texcoords) ZGV.SlideBar.Capture() elseif TargetFrame:IsVisible() then self:Show() end else if ZGV.SlideBar then ZGV.SlideBar.RemoveButton(self:GetName()) ZGV.SlideBar.Release() elseif TargetFrame:IsVisible() then self:Hide() end end end
